import React, {useState } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { addTransaction } from '../Redux/Action/ActionCreator'

const AddExpense = ({transactions, addTransaction}) =>  {
    const [text, setText] = useState("")
    const [amount, setAmount] = useState(0)

    const addTransactionMain = transaction => {
        addTransaction(transaction)
    }
    const id = transactions[0] ? transactions[0].id + 1 : 1

 const handleSubmit = event => {
            event.preventDefault()
            const addedTransaction = {
                id: id,
                text:text,
                amount: amount
            }
            addTransactionMain(addedTransaction)
            clearInput()
        }

    const clearInput = () => {
            setAmount(0)
            setText("")
        }

        return (
            <div>
                <Link to="/">
                <button>Back</button>
                </Link>
                <h1>Add Transaction</h1>
                <form onSubmit={handleSubmit}>
                <div className="form-control">
                        <label htmlFor="option">Expence type</label>
                        <select id="option">
                            <option value="Job"></option>
                        </select>
                    </div>
                    <div className="form-control">
                        <label htmlFor="type">Expence type</label>
                        <input 
                        type="text" 
                        id="type" 
                        name="expence-type" 
                        placeholder="Expence type"
                        autoComplete="off"
                        value={text}
                        onChange={e => setText(e.target.value)}/>
                    </div>
                    <div className="form-control">
                        <label htmlFor="amount">Amount</label>
                        <input
                         type="number"
                         id="amount"
                         name="expence-amount" 
                         placeholder="Expence amount"
                         autoComplete="off"
                         value={amount}
                         onChange={e => setAmount( e.target.value)}
                         />
                    </div>
                    <button type="submit">Add Transaction</button>
                </form>
            </div>
        )}


const mapStateToProps = state => ({
    transactions: state.transaction
})

const mapDispatchToProps = dispatch => ({
    addTransaction: (transaction) => dispatch(addTransaction(transaction))
})
export default connect(mapStateToProps, mapDispatchToProps)(AddExpense)
